<?php
class Add_marksheet_Modal extends CI_Model{	function add_marksheet_do($student_name,$student_class,$roll_no,$maths_marks,$phy_marks,$chem_marks,$hindi_marks,$english_marks,$passport_size_photo,$total_ontained_marks,$total_score_percentage)    {				$query = $this->db->query("insert into student_profile_with_marks(stud_name,class,roll_no,passport_size_image,maths_marks,phy_marks,chem_marks,hindi_marks,english_marks,total_ontained_marks,total_score_percentage) values('".$student_name."','".$student_class."','".$roll_no."','".$passport_size_photo."','".$maths_marks."','".$phy_marks."','".$chem_marks."','".$hindi_marks."','".$english_marks."','".$total_ontained_marks."','".$total_score_percentage."')");		if ($query !== FALSE)		{			return "Saved";		}    }		}
?>