<?php
class Add_marksheet_Modal extends CI_Model{	function add_marksheet_do($student_name,$student_class,$roll_no,$maths_marks,$phy_marks,$chem_marks,$hindi_marks,$english_marks,$passport_size_photo,$total_ontained_marks,$total_score_percentage)    {				$data = array('stud_name' => $student_name,'class' => $student_class,'roll_no' => $roll_no,'passport_size_image' => $passport_size_photo,'maths_marks' => $maths_marks,'phy_marks' => $phy_marks,'chem_marks' => $chem_marks,'hindi_marks' => $hindi_marks,'english_marks' => $english_marks,'total_ontained_marks' => $total_ontained_marks,'total_score_percentage' => $total_score_percentage);		$query = $this->db->insert('student_profile_with_marks',$data);				if ($query !== FALSE)		{			return "Saved";		}    }		function chk_roll_no_existance($roll_no)    {				$this->db->select('*');		$this->db->from('student_profile_with_marks');		$array = array('roll_no' => $roll_no);		$this->db->where($array); 		$query = $this->db->get();        return $query->num_rows();    }	    function fetch_getstudents_count($search_all)    {				$this->db->select('*');		$this->db->from('student_profile_with_marks');		$this->db->like('stud_name', $search_all);		$this->db->order_by("total_score_percentage", "desc");		$query = $this->db->get();        return $query->num_rows();    }		function fetch_getstudents($limit, $start, $search_all)    {				if ($search_all == "NULL") $search_all = "";		$this->db->select('*');		$this->db->from('student_profile_with_marks');		$this->db->like('stud_name', $search_all);		$this->db->order_by('total_score_percentage', 'desc');		$this->db->limit($limit, $start);				$query = $this->db->get();		return $query->result();    }			    function fetch_getstudents_count_param($search_all,$stud_name,$class,$roll_no,$total_score_percentage)    {				$array = array('stud_name' => $search_all,'stud_name' => $stud_name,'class' => $class,'roll_no' => $roll_no,'total_score_percentage' => $total_score_percentage);		$this->db->select('*');		$this->db->from('student_profile_with_marks');		$this->db->like($array);		$this->db->order_by('total_score_percentage', 'desc');				$query = $this->db->get();        return $query->num_rows();    }		function fetch_getstudents_param($limit, $start,$search_all,$stud_name,$class,$roll_no,$total_score_percentage)    {				if ($search_all == "NULL") $search_all = "";						$array = array('stud_name' => $search_all,'stud_name' => $stud_name,'class' => $class,'roll_no' => $roll_no,'total_score_percentage' => $total_score_percentage);		$this->db->select('*');		$this->db->from('student_profile_with_marks');		$this->db->like($array);		$this->db->order_by('total_score_percentage', 'desc');		$this->db->limit($limit, $start);				$query = $this->db->get();		return $query->result();										    }		}
?>