<?phpclass AdminProducts_Modal extends CI_Model{		    function fetch_unit_in_groupoption_mdl()    {				$query = $this->db->query("CALL fetch_mstore_units_in_option()");        return $query->result();    }		    function fetch_product_group_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_product_group_in_option_product_page('".$user_id."')");        return $query->result();    }		    function fetch_tax_category_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_tax_in_tbody('".$user_id."')");        return $query->result();    }		    function fetch_admin_mat_ctr_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_admin_mat_ctr_in_option('".$user_id."')");        return $query->result();    }		function btn_save_product_mdl($product_name,$product_barcode,$alias,$product_group,$unit,$expiry_date,$manufacturing_purchase_price,$mrp_price,$sales_price,$sale_profit,$distribution_price,$Stockist_sale_profit,$sb_sale_profit,$tax_cat,$hsn_code,$mat_center,$open_stock_qty,$min_stock,$reorder_level,$reorder_qty,$supply_type,$description,$tax_included_sale,$per_rupee_flag,$user_id,$batch_enable)    {				$query=$this->db->query("CALL save_admin_product_details('".$user_id."','".$product_name."','".$product_barcode."','".$alias."','".$product_group."','".$unit."','".$expiry_date."','".$manufacturing_purchase_price."','".$mrp_price."','".$sales_price."','".$sale_profit."','".$distribution_price."','".$Stockist_sale_profit."','".$sb_sale_profit."','".$tax_cat."','".$hsn_code."','".$mat_center."','".$open_stock_qty."','".$min_stock."','".$reorder_level."','".$reorder_qty."','".$supply_type."','".$description."','".$tax_included_sale."','".$per_rupee_flag."','".$batch_enable."')");				$query2=$this->db->query("CALL fetch_admin_product_details_id_for_product_stock('".$user_id."','".$product_name."')");		 return $query2->result();		    }		function btn_save_product_stock_mdl($user_id,$product_id,$product_barcode,$open_stock_qty,$mat_center_id)	{		$query = $this->db->query("CALL save_mstore_product_stock('".$user_id."','".$product_id."','".$product_barcode."','".$open_stock_qty."','".$mat_center_id."')");	}		function btn_update_product_mdl($product_name,$product_barcode,$alias,$product_group,$unit,$expiry_date,$manufacturing_purchase_price,$mrp_price,$sales_price,$sale_profit,$distribution_price,$Stockist_sale_profit,$sb_sale_profit,$tax_cat,$hsn_code,$mat_center,$open_stock_qty,$min_stock,$reorder_level,$reorder_qty,$supply_type,$description,$tax_included_sale,$per_rupee_flag,$user_id,$product_id,$batch_enable,$unit_detail)	{		$query = $this->db->query("CALL update_admin_product_details('".$user_id."','".$product_name."','".$product_barcode."','".$alias."','".$product_group."','".$unit."','".$expiry_date."','".$manufacturing_purchase_price."','".$mrp_price."','".$sales_price."','".$sale_profit."','".$distribution_price."','".$Stockist_sale_profit."','".$sb_sale_profit."','".$tax_cat."','".$hsn_code."','".$mat_center."','".$open_stock_qty."','".$min_stock."','".$reorder_level."','".$reorder_qty."','".$supply_type."','".$description."','".$tax_included_sale."','".$per_rupee_flag."','".$product_id."','".$batch_enable."','".$unit_detail."')");	}			    function fetch_product_in_tbody_mdl($user_id)    {				$query = $this->db->query("CALL fetch_admin_product_in_tbody('".$user_id."')");        return $query->result();    }		function fetch_product_in_edit_mdl($user_id,$product_id)	{		$query = $this->db->query("CALL fetch_admin_product_edit('".$user_id."','".$product_id."')");		return $query->result();    }	 	function fetch_product_detail_inreport_tbody_mdl($user_id,$short_by,$search_btn)	{		if($short_by==0)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==1)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_expiry('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==2)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_quantity('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==3)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_price('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==4)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_demand('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}    }	 		function products_expiration_in_tbody_mdl($user_id,$today,$datefrommonthago)	{		$query = $this->db->query("CALL fetch_products_expiration_by_days('".$user_id."','".$today."','".$datefrommonthago."')");		return $query->result();    }	 }?>