<?phpclass AdminStock_Modal extends CI_Model{		    function admin_fetch_stockiout_mdl($user_id)    {				$query = $this->db->query("CALL fetch_admin_stockiout('".$user_id."')");        return $query->result();    }    function fetch_pros_ttl_qty_mdl($user_id,$pros_id)    {				$query = $this->db->query("CALL fetch_admin_prod_ttl_qty('".$user_id."','".$pros_id."')");        return $query->result();    }		    }?>