<?phpclass Adminsave_user_signup_Modal extends CI_Model{	/*--- for inserting news---*/	function save_user_signup_mdl($user_name,$user_mobile,$user_mail,$user_address,$user_pass)	{		$query = $this->db->query("CALL app_user_signup('".$user_name."','".$user_mobile."','".$user_mail."','".$user_address."','".$user_pass."')"); 		if($query)		{			return '1';		}	}		function check_user_login_mdl($user_mobile,$user_pass)	{		$query = $this->db->query("CALL app_user_login('".$user_mobile."','".$user_pass."')"); 		return $query->result();	}		function update_muser_password_mdl($muser_id,$user_pass)	{		$query = $this->db->query("CALL update_muser_password('".$muser_id."','".$user_pass."')"); 		if($query)		{			$query_fetch_all = $this->db->query("CALL app_user_details('".$muser_id."')"); 			return $query_fetch_all->result();			// return '1';		}	}			function update_muser_acc_details_mdl($muser_id,$muser_name,$muser_email,$muser_address,$user_pass)	{		$query = $this->db->query("CALL update_muser_account_details('".$muser_id."','".$muser_name."','".$muser_email."','".$muser_address."','".$user_pass."')"); 		if($query)		{			return '1';		}	}		function chk_user_send_otp_signup_mdl($user_mobile)	{		$query = $this->db->query("CALL app_user_availability('".$user_mobile."')"); 		return $query->result();	}			function fetch_muser_profile_mdl($muser_id)	{		$query = $this->db->query("CALL fetch_muser_profile_details('".$muser_id."')"); 		return $query->result();	}			function check_existing_mobile_user_signup_mdl($user_mobile)	{		$query = $this->db->query("CALL app_user_login('".$user_mobile."')"); 		return $query->result();	}		}?>