<?phpclass DepoPurchseOrder_Modal extends CI_Model{		    function add_new_product_to_request_mdl($user_id,$order1_id,$request_to_id,$pro_id,$barcode_no,$pro_qty,$pro_price,$pro_tot_amt,$all_new_pro_tot_amt)    {				$query = $this->db->query("CALL add_new_product_to_request('".$user_id."','".$order1_id."','".$request_to_id."','".$pro_id."','".$barcode_no."','".$pro_qty."','".$pro_price."','".$pro_tot_amt."','".$all_new_pro_tot_amt."')");    }		    function fetch_product_details_by_proname_to_add_request($pro_name_add_id,$user_id)    {				$query = $this->db->query("CALL fetch_admin_product_details_by_proname_to_add_request('".$pro_name_add_id."','".$user_id."')");        return $query->result();    }		    function fetch_product_details_by_barcode_to_add_request($barcode_no,$user_id)    {				$query = $this->db->query("CALL fetch_admin_product_details_by_barcode_to_add_request('".$barcode_no."','".$user_id."')");        return $query->result();    }		    function all_purchase_order_depo_mdl($user_id)    {				$query = $this->db->query("CALL fetch_all_purchase_order('".$user_id."')");        return $query->result();    }		    function delete_self_pending_request_product_mdl($ord2_id,$order_id,$total_amt_of_order)    {				$query = $this->db->query("CALL delete_self_request_pending_products('".$ord2_id."','".$order_id."','".$total_amt_of_order."')");    }				    function update_self_pending_request_product_mdl($proid,$order_id,$updated_qty,$updated_tot_price,$total_amt_of_order)    {				$query = $this->db->query("CALL update_self_request_pending_products('".$proid."','".$order_id."','".$updated_qty."','".$updated_tot_price."','".$total_amt_of_order."')");    }		    function fetch_self_requested_products_by_ord_details_mdl($order_id,$request_to)    {				$query = $this->db->query("CALL fetch_self_requested_products_details('".$order_id."','".$request_to."')");        return $query->result();    }		    function all_my_product_request($user_id)    {				$query = $this->db->query("CALL fetch_franchise_products_requests_order('".$user_id."')");        return $query->result();    }		    function fetch_order_details_mdl($order_id,$user_id)    {				$query = $this->db->query("CALL fetch_purchase_order_details('".$order_id."','".$user_id."')");        return $query->result();    }		    function confirm_order_all_mdl($order_id)    {				$this->db->query("CALL confirm_order_all('".$order_id."')");        // return $query->result();    }		    function stockin_to_child_mdl($child_id,$proid,$bcode,$pqty,$in_out_state,$mat_ctr,$order_id)    {				$this->db->query("CALL save_stockin_to_child('".$child_id."','".$proid."','".$bcode."','".$pqty."','".$in_out_state."','".$mat_ctr."','".$order_id."')");    }	    function stockout_from_parent_mdl($user_id,$proid,$bcode,$pqty,$in_out_state,$mat_ctr,$order_id)    {				$this->db->query("CALL save_stockout_from_parent('".$user_id."','".$proid."','".$bcode."','".$pqty."','".$in_out_state."','".$mat_ctr."','".$order_id."')");    }}?>