<?php
class Location_Modal extends CI_Model{	protected $table = 'location';    function get_locality_count($search_all)    {		        $sql = "select * from location where location_name like '%$search_all%'";        $query = $this->db->query($sql);        return $query->num_rows();				 // return $this->db->count_all($this->table);    }					public function get_localities($limit, $start) {        $this->db->limit($limit, $start);        $query = $this->db->get($this->table);        return $query->result();    }					    function fetch_locality_pagination($limit, $start, $search_all)    {				if ($search_all == "NULL") $search_all = "";		$sql = "SELECT * FROM location,city WHERE location.city_id=city.city_id and location_name like '%$search_all%' order by location_id DESC limit " . $start . ", " . $limit;		$query = $this->db->query($sql);		return $query->result();    }							    function fetch_all_cities_mdl()    {				$query = $this->db->query("SELECT * FROM city order by city_id DESC");        return $query->result();		    }				    function fetch_all_location_by_citie_mdl($slt_city)    {				$query = $this->db->query("SELECT * FROM location where city_id='".$slt_city."'");        return $query->result();		    }		    function get_all_localities_mdl()    {				$query = $this->db->query("SELECT * FROM location,city WHERE location.city_id=city.city_id order by location_id DESC");        return $query->result();		    }		    function btn_update_city($txt_city,$txt_city_id)    {				$query = $this->db->query("UPDATE city set city_name='".$txt_city."' WHERE city_id='".$txt_city_id."'");		if ($query !== FALSE)		{			return "City has been Updated...";			// success so access your result		}		else		{			return "City has not been Updated";			// do something with $this->db->error()		}        // return $query->result();		    }		    function update_locality($txt_city,$txt_locality,$txt_locality_id)    {				$query = $this->db->query("UPDATE location set location_name='".$txt_locality."',city_id='".$txt_city."' WHERE location_id='".$txt_locality_id."'");		if ($query !== FALSE)		{			return "Location has been Updated...";			// success so access your result		}		else		{			return "Location has not been Updated";			// do something with $this->db->error()		}		    }		    function delete_city($got_del_id)    {				$query = $this->db->query("DELETE FROM city WHERE city_id='".$got_del_id."'");        // return $query->result();		    }		    function delete_locality($got_del_id)    {				$query = $this->db->query("DELETE FROM location WHERE location_id='".$got_del_id."'");        // return $query->result();		    }		    function get_city_for_edit_mdl($got_edit_id)    {				$query = $this->db->query("SELECT * FROM city where city_id='".$got_edit_id."'");        return $query->result();		    }    function get_locality_for_edit_mdl($got_edit_id)    {				$query = $this->db->query("SELECT * FROM location where location_id='".$got_edit_id."'");        return $query->result();		    }
	/*--- for inserting news---*/
