<?phpclass MStoreBillSeries_Modal extends CI_Model{		function save_bill_series_detail_autometic_mdl($user_id,$bill_series_name,$bill_series_for,$voucher_numering,$send_sms,$send_email,$print_voucher_on_save,$print_barcode_on_save,$show_opt_fields,$v_number_prefix,$prefix_simple_text,$v_number_midText,$midText_simple_text,$v_number_postfix,$postfix_simple_text)    {				$query = $this->db->query("CALL save_mstore_bill_series_detail_autometic('".$user_id."','".$bill_series_name."','".$bill_series_for."','".$voucher_numering."','".$send_sms."','".$send_email."','".$print_voucher_on_save."','".$print_barcode_on_save."','".$show_opt_fields."','".$v_number_prefix."','".$prefix_simple_text."','".$v_number_midText."','".$midText_simple_text."','".$v_number_postfix."','".$postfix_simple_text."')");    }			function save_bill_series_detail_manual_mdl($user_id,$bill_series_name,$bill_series_for,$voucher_numering,$send_sms,$send_email,$print_voucher_on_save,$print_barcode_on_save,$show_opt_fields)    {				$query = $this->db->query("CALL save_mstore_bill_series_detail_manual('".$user_id."','".$bill_series_name."','".$bill_series_for."','".$voucher_numering."','".$send_sms."','".$send_email."','".$print_voucher_on_save."','".$print_barcode_on_save."','".$show_opt_fields."')");    }				    function fetch_bill_series_in_tbody_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_bill_series_in_tbody('".$user_id."')");        return $query->result();    }	}?>