<?phpclass MStoreBillSundry_Modal extends CI_Model{		function save_bill_sundry_absolute_amt_mdl($user_id,$sun_name,$print_name,$sundry_type,$default_val,$sundry_nature,$billSunAppliedAs)    {				$query = $this->db->query("CALL save_mstore_bill_sundry_absolute_amt('".$user_id."','".$sun_name."','".$print_name."','".$sundry_type."','".$default_val."','".$sundry_nature."','".$billSunAppliedAs."')");    }		function save_bill_sundry_percentage_amt_mdl($user_id,$sun_name,$print_name,$sundry_type,$default_val,$sundry_nature,$billSunAppliedAs,$percent_of,$perc_amt_of,$roundSudryAmtChk,$roundNearOfVal)    {				$query = $this->db->query("CALL save_mstore_bill_sundry_percentage_amt('".$user_id."','".$sun_name."','".$print_name."','".$sundry_type."','".$default_val."','".$sundry_nature."','".$billSunAppliedAs."','".$percent_of."','".$perc_amt_of."','".$roundSudryAmtChk."','".$roundNearOfVal."')");    }			    function fetch_bill_sundry_in_tbody_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_bill_sundry_in_tbody('".$user_id."')");        return $query->result();    }	}?>