<?phpclass MStoreProducts_Modal extends CI_Model{		    function fetch_unit_in_groupoption_mdl()    {				$query = $this->db->query("CALL fetch_mstore_units_in_option()");        return $query->result();    }		    function fetch_trans_details_prods_mdl($user_id,$prod_id)    {				$query = $this->db->query("CALL fetch_mstore_trans_details_prods('".$user_id."','".$prod_id."')");        return $query->result();    }		    function fetch_product_group_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_product_group_in_option_product_page('".$user_id."')");        return $query->result();    }		    function fetch_tax_category_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_tax_in_tbody('".$user_id."')");        return $query->result();    }		function btn_save_product_mdl($product_name,$product_barcode,$alias,$print_name,$product_group,$unit,$open_stock_qty,$open_stock_val,$min_op_stock,$tax_cat,$hsn_code,$sales_price,$purchase_price,$mrp_price,$min_sales_price,$sales_discount,$purchase_discount,$sale_profit,$supply_type,$description,$user_id,$tax_included_sale,$tax_included_purchse,$batch_enable,$unit_detail)    {				$query=$this->db->query("CALL save_mstore_product_details('".$user_id."','".$product_name."','".$product_barcode."','".$alias."','".$print_name."','".$product_group."','".$unit."','".$open_stock_qty."','".$open_stock_val."','".$min_op_stock."','".$tax_cat."','".$hsn_code."','".$sales_price."','".$purchase_price."','".$mrp_price."','".$min_sales_price."','".$sales_discount."','".$purchase_discount."','".$supply_type."','".$description."','".$tax_included_sale."','".$tax_included_purchse."','".$batch_enable."','".$unit_detail."')");				$query2=$this->db->query("CALL fetch_product_details_id_for_product_stock('".$user_id."','".$product_name."')");		 return $query2->result();		    }		function btn_save_product_stock_mdl($user_id,$product_id,$product_barcode,$open_stock_qty,$mat_center_id)	{		$query = $this->db->query("CALL save_mstore_product_stock('".$user_id."','".$product_id."','".$product_barcode."','".$open_stock_qty."','".$mat_center_id."')");	}		function btn_update_product_mdl($product_id,$product_name,$product_barcode,$alias,$print_name,$product_group,$unit,$open_stock_qty,$open_stock_val,$min_op_stock,$tax_cat,$hsn_code,$sales_price,$purchase_price,$mrp_price,$min_sales_price,$sales_discount,$purchase_discount,$sale_profit,$supply_type,$description,$user_id,$tax_included_sale,$tax_included_purchse,$batch_enable,$unit_detail)	{		$query = $this->db->query("CALL update_mstore_product_details('".$product_id."','".$user_id."','".$product_name."','".$product_barcode."','".$alias."','".$print_name."','".$product_group."','".$unit."','".$open_stock_qty."','".$open_stock_val."','".$min_op_stock."','".$tax_cat."','".$hsn_code."','".$sales_price."','".$purchase_price."','".$mrp_price."','".$min_sales_price."','".$sales_discount."','".$purchase_discount."','".$supply_type."','".$description."','".$tax_included_sale."','".$tax_included_purchse."','".$batch_enable."','".$unit_detail."')");	}			    function fetch_product_in_tbody_mdl($user_id)    {		$query = $this->db->query("CALL fetch_mstore_product_in_tbody(".$user_id.")");        return $query->result();    }		function fetch_product_in_edit_mdl($user_id,$product_id)	{		$query = $this->db->query("CALL fetch_mstore_product_edit('".$user_id."','".$product_id."')");		// return "dsdf";		return $query->result();    }	 	function fetch_product_detail_inreport_tbody_mdl($user_id,$short_by,$search_btn)	{		if($short_by==0)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==1)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_expiry('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==2)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_quantity('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==3)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_price('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}		else if($short_by==4)		{			$query = $this->db->query("CALL fetch_mstore_product_detail_inreport_demand('".$user_id."','".$short_by."','".$search_btn."')");			return $query->result();		}    }	 		function fetch_admin_product_detail_in_tbody($user_id)	{		$query = $this->db->query("CALL fetch_admin_product_detail_in_childs('".$user_id."')");		return $query->result();    }	 }?>