<?php
class MStoreProducts_Modal extends CI_Model{
		    function fetch_unit_in_groupoption_mdl()    {				$query = $this->db->query("CALL fetch_mstore_units_in_option()");        return $query->result();    }		    function fetch_product_group_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_product_group_in_option('".$user_id."')");        return $query->result();    }		    function fetch_tax_category_in_groupoption_mdl($user_id)    {				$query = $this->db->query("CALL fetch_mstore_tax_in_tbody('".$user_id."')");        return $query->result();    }		function btn_save_product_mdl($product_name,$product_barcode,$alias,$print_name,$product_group,$unit,$open_stock_qty,$open_stock_val,$min_op_stock,$tax_cat,$hsn_code,$sales_price,$purchase_price,$mrp_price,$min_sales_price,$sales_discount,$purchase_discount,$sale_profit,$supply_type,$description,$user_id)    {				$query = $this->db->query("CALL save_mstore_product_details('".$user_id."','".$product_name."','".$product_barcode."','".$alias."','".$print_name."','".$product_group."','".$unit."','".$open_stock_qty."','".$open_stock_val."','".$min_op_stock."','".$tax_cat."','".$hsn_code."','".$sales_price."','".$purchase_price."','".$mrp_price."','".$min_sales_price."','".$sales_discount."','".$purchase_discount."','".$supply_type."','".$description."')");		    }			    function fetch_product_in_tbody_mdl($user_id,$lim1,$lim2,$txt_search_product)    {				$query = $this->db->query("CALL fetch_mstore_product_in_tbody('".$user_id."','".$lim1."','".$lim2."','".$txt_search_product."')");        return $query->result();    }			}
?>